<template>
     <v-card>
        <v-card-title>
        Prestamos
        <v-btn icon  @click="download">
            <v-icon color="green">
                fa-file-excel-o
            </v-icon>
        </v-btn>
        <v-dialog
          v-model="dialog"
          hide-overlay
          persistent
          width="300"
        >
          <v-card
            color="primary"
            dark
          >
            <v-card-text>
              Por favor espere
              <v-progress-linear
                indeterminate
                color="white"
                class="mb-0"
              ></v-progress-linear>
            </v-card-text>
          </v-card>
        </v-dialog>
        <v-spacer></v-spacer>
           <v-flex xs1 sm1 md1>
                <v-combobox
                v-model="paginationRows"
                :items="pagination_select"
                label="Mostrar Registros"
                @change="search()"
                ></v-combobox>
                
        </v-flex>
        </v-card-title>
        <v-data-table
        :headers="headers"
        :items="amortizations"
         hide-actions
        >
        <template slot="headers" slot-scope="props" >
           <tr>
                <th v-for="(header,index) in props.headers" :key="index" class="text-xs-left">
                    
                        <v-flex v-if="header.value!='actions'">
                            <v-tooltip bottom>
                                <span slot="activator">{{header.text}}</span>
                                <span >{{header.input}}</span>
                            </v-tooltip>
                            <v-menu  v-model="header.menu"
                                    :close-on-content-click="false"
                                    >
                                    <v-btn
                                        slot="activator"
                                        icon
                                        v-if="header.sortable!=false"
                                    >
                                    <v-icon  small :color="header.input!=''?'blue':'black'">fa-filter</v-icon>
                                    </v-btn>
                                    <v-card  v-if="header.type=='text'">
                                        <v-text-field
                                            outline
                                            hide-details
                                            v-model="header.input"
                                            append-icon="search"
                                            :label="`Buscar ${header.text}...`"
                                            @keydown.enter="search()"
                                            @keyup.delete="checkInput(header.input)"
                                            @keyup.esc="header.menu=false"
                                        ></v-text-field>
                                    
                                    </v-card>
                                    <v-card  v-if="header.type=='date'">
                                        
                                        <v-list>
                                            <v-list-tile avatar>
                                                <v-list-tile-content>
                                                    <v-menu
                                                        
                                                        :close-on-content-click="false"
                                                        v-model="menu_date"
                                                        :nudge-right="40"
                                                        lazy
                                                        transition="scale-transition"
                                                        offset-y
                                                        full-width
                                                        max-width="290px"
                                                        min-width="290px"
                                                    >
                                                    
                                                    <v-text-field
                                                        hide-details
                                                        slot="activator"
                                                        v-model="header.input"
                                                        :label="`Buscar ${header.text}...`"
                                                        readonly
                                                    ></v-text-field>
                                                    <v-date-picker v-model="header.input" no-title @input="menu_date = false"></v-date-picker>
                                                
                                                    </v-menu>          
                                                </v-list-tile-content>

                                                <v-list-tile-avatar>
                                                <v-icon @click="clearDate(index)">delete</v-icon>
                                                <v-icon @click="search()">search</v-icon>
                                                </v-list-tile-avatar>

                                            </v-list-tile>
                                            </v-list>

                                    </v-card>
                            </v-menu>
                            <!-- <v-icon small @click="toggleOrder(header.value)" v-if="header.value == filterName ">{{pagination.descending==false?'arrow_upward':'arrow_downward'}}</v-icon> -->
                        </v-flex>
                </th>
           </tr>
        </template>
        <template slot="items"  slot-scope="props">
            <td class="text-xs-left">{{ props.item.PresNumero }}</td>
            <td class="text-xs-left">{{ props.item.PresFechaDesembolso }}</td>
            <td class="text-xs-left">{{ props.item.PadTipo }}</td>
            <td class="text-xs-left">{{ props.item.PadMatricula }}</td>
            <td class="text-xs-left">{{ props.item.PadMatriculaTit }}</td>
            <td class="text-xs-left">{{ props.item.PadCedulaIdentidad }}</td>
            <td class="text-xs-left">{{ props.item.PadExpCedula }}</td>
            <td class="text-xs-left">{{ props.item.PadNombres }}</td>
            <td class="text-xs-left">{{ props.item.PadNombres2do }}</td>
            <td class="text-xs-left">{{ props.item.PadPaterno }}</td>
            <td class="text-xs-left">{{ props.item.PadMaterno }}</td>
           
            <td class="text-xs-left">{{ props.item.PresCtbNroCpte }}</td>
            <td class="text-xs-left"> <a  v-bind:href="generate_link(props.item.IdPrestamo)"><v-icon>assignment</v-icon></a> 
                <v-btn icon @click="makePDF(props.item.IdPrestamo)"><v-icon color="info">insert_drive_file</v-icon></v-btn>
            </td>
            
        </template>

        </v-data-table>
        
        <div class="text-xs-center">
            <v-pagination
            v-model="page"
            :length="last_page"
            :total-visible="7"
             @input="next"
            ></v-pagination>
        </div>   
         <div class="text-xs-right">
            
            <v-flex xs11 sm11 md11>
                Mostrando {{from}}-{{to}} de {{total}} registros 
            </v-flex>

        </div>
        <br>


        <v-dialog v-model="show_certificate" max-width="800px" max-high="40px">
            <v-card>
            <v-card-text >
                <v-container grid-list-md>
                <v-btn @click="makePDF(prestamo_id)">Recargar</v-btn>
                <v-text-field
                    label="fecha"
                    v-model="certification.fecha_texto"
                ></v-text-field>
                <v-text-field
                    label="Nro Hoja"
                    v-model="certification.hoja"
                ></v-text-field>
                <v-text-field
                    label="Porcentage"
                    v-model="certification.porcentaje"
                ></v-text-field>
                <v-text-field
                    label="Parrafo"
                    v-model="certification.parrafo"
                ></v-text-field>
                <v-text-field
                    label="Texto Mediante"
                    v-model="certification.texto_mediante"
                ></v-text-field>
                <!-- <v-text-field  label="Fecha"><v-text-field> -->
                <!-- <v-text-field v-model="certification.hoja" label="Nro Hoja"><v-text-field> -->
                <div class="embed-container">
                    <iframe frameborder="0" width="700" height="500" :src="cadena"></iframe>
                </div>
                </v-container>
            </v-card-text>

     
            </v-card>
        </v-dialog>  

    </v-card>
</template>
<script>
import * as jsPDF from 'jspdf';
require('jspdf-autotable');
export default {
    data () {
      return {
        dialog: false,
        menu_date: false,
        // date:false,
        pagination: {
          sortBy: 'PresNumero'
        },
        headers: [
            { text: 'Nro Prestamo', value: 'PresNumero',input:'' , menu:false,type:"text"},
            { text: 'Fecha Desembolso', value: 'PresFechaDesembolso',input:'' , menu:false,type:"date"},
            { text: 'Tipo', value: 'PadTipo',input:'', menu:false ,type:"text"},
            { text: 'Matricula', value: 'PadMatricula' ,input:'', menu:false,type:"text"},
            { text: 'Matricula Titular', value: 'PadMatriculaTit' ,input:'', menu:false,type:"text"},
            { text: 'CI', value: 'PadCedulaIdentidad',input:'' , menu:false,type:"text"},
            { text: 'Exp', value: 'PadExpCedula',input:'' , menu:false,type:"text"},
            { text: '1er Nombre', value: 'PadNombres',input:'' , menu:false,type:"text"},
            { text: '2do Nombre', value: 'PadNombres2do',input:'' , menu:false,type:"text"},
            { text: 'Ap. Paterno', value: 'PadPaterno',input:'', menu:false,type:"text"},
            { text: 'Ap. Materno',value:'PadMaterno',input:'', menu:false,type:"text"},
            { text: 'Nro Comprobante',value:'PresCtbNroCpte',input:'', menu:false,type:"text"},
            { text: 'Accion',value:'actions',input:'', menu:false,type:"text"},
        ],
        amortizations: [],
        loading: true,
        last_page:1,
        total:0,
        from:0,
        to:0,
        page:1,
        paginationRows: 10,
        pagination_select:[10,20,30],
        show_certificate:false,
        cadena:null,
        prestamo_id:'',
        certification:{
            grado:'',
            nombre:'',
            producto:'',
            tipo:' largo plazo',
            prestamo:'',
            amoritzacion: null,
            date_literal:'',
            money_literal:'',
            today_literal:'',
            fecha_texto:'',
            hoja:'',
            parrafo:'',
            texto_mediante:''
        }
      }
    },
    mounted()
    {
        this.search();
    },
    created(){
        //   axios.get('/api/amortizacion/create')
        //         .then((response) => {                                       
        //             this.newProvider = response.data.amortizacion; 
        //             this.newContact = response.data.contact; 
        //         });    
    },
    methods:{
        
        getData(url,parameters){
            return new Promise((resolve,reject)=>{
               this.loading = true;
               axios.get(url,{
                        params:parameters
                    })
                    .then((response) => {
                        this.loading = false;
                        resolve(response.data);
                    });
            });
        },
        next(page){
            this.page = page;
            this.search();
        },
        search(){
            
            return new Promise((resolve,reject)=>{   
                this.getData('/api/loans',this.getParams()).then((data)=>{
                    this.amortizations = data.data;
                    this.last_page = data.last_page;
                    this.total = data.total;
                    this.from = data.from;
                    this.to = data.to;
                    // this.page = 1   ;
                    resolve();
                });
            });
        },
        closeDate(index){
            this.headers[index].menu = false;
            this.search();
            // console.log(this.headers[index]);
            
        },
        clearDate(index){
            this.headers[index].input='';
            this.search();
        },
        getParams(){
            let params={};
            this.headers.forEach(element => {
                params[element.value] = element.input.toUpperCase();
            });
            // params['sorted']=this.filterName;
            // params['order']=this.pagination.descending==true?'asc':'desc';
            params['page']=this.page;
            params['pagination_rows']=this.paginationRows;
            return params;
        },
        checkInput(search)
        {
            if(search=='')
            {
                this.search();
            }
        },
        download: function (event) {
            // `this` inside methods point to the Vue instance
            self = this;
            self.dialog = true
            //  self.dialog = true;
            let parameters = this.getParams();
            parameters.excel =true;
            console.log(parameters);
            axios({
                url: '/api/loans',
                method: 'GET',
                params: parameters,
                responseType: 'blob', // important
            }).then((response) => {
                const url = window.URL.createObjectURL(new Blob([response.data]));
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', 'Prestamos'+moment().format()+'.xls');
                document.body.appendChild(link);
                link.click();
                self.dialog = false;
            });
        },
        generate_link(id){
            return 'http://sismu.muserpol.gob.bo/musepol/akardex.aspx?'+id;
            //console.log(this.loans)
        },
        formatDate (date) {
            if (!date) return null

            const [year, month, day] = date.split('-')
            return `${month}/${day}/${year}`
        },
        parseDate (date) {
            if (!date) return null

            const [month, day, year] = date.split('/')
            return `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`
        },
        makePDF(id_prestamo){
           this.prestamo_id = id_prestamo;
            axios({
                url: '/api/certificate_info?id_prestamo='+id_prestamo,
                method: 'GET',
            }).then((response) => {
                    console.log(response.data);
                    this.certification.grado = response.data.affiliate.grado;
                    this.certification.nombre = (response.data.affiliate.first_name || '') +' '+(response.data.affiliate.second_name || '' ) + ' '+(response.data.affiliate.last_name || '' )+' '+(response.data.affiliate.mother_last_name || '' )+' con C.I. '+(response.data.affiliate.identity_card || '' ) +' '+(response.data.affiliate.ext || '');
                    this.certification.amortizacion = response.data.amortizacion;
                    this.certification.producto = response.data.prestamo.producto;
                    this.certification.prestamo = response.data.prestamo.PresNumero;
                    this.certification.date_literal = response.data.literal_date;
                    this.certification.money_literal = response.data.literal_money;
                    this.certification.today_literal = response.data.literal_today;

                    console.log('ingresando');
                    console.log(this.certification.amortizacion);
                   
                    var d = new jsPDF();
                    this.show_certificate = true;
                    let logo = 'data:image/jpeg;base64,';

                    
                    console.log('mostrando pdf hdp');
                    d.addImage(logo, 'JPEG', 20,25);
                    let leyenda = 'LA SUSCRITA ENCARGADO DE REGISTRO, CONTROL Y RECUPERACION DE PRESTAMOS, DE LA MUTUAL DE SERVICIOS AL POLICIA "MUSERPOL", EN USO DE SUS ATRIBUCIONES, EN CUANTO PUEDE Y EL DERECHO LE PERMITE:'; 
                    let splitTitle = d.splitTextToSize(leyenda, 150);
                    d.setFontSize(10)
                    d.text(95,25,splitTitle);
                    
                    var x = 15;
                    var y =60;

                    d.text('C E R T I F I C A:',x,y);
                    let p1 = 'Que, previa revisión y verificación de la base de datos del Sistema de Préstamo de la Entidad  "SISMU", del Señor(a) afiliado, se VERIFICO que:';
                    let spl1 = d.splitTextToSize(p1,180);
                    d.text(x,y+10,spl1);
                    y+=10
                    d.setFontType("bold");
                    d.text('El (la) Sr (a). '+this.certification.nombre ,x+20,y+10,);
                    y+=10
                    d.setFontType("normal");
                    let p2 = 'Siendo así se encuentra en situación de mora su préstamo con garantia '+this.certification.producto.trim()+ ' registrado con el Nro. '
                    +this.certification.prestamo+' desde '+this.certification.date_literal +' y siendo su saldo según ultimo registro de pago Bs. '+this.certification.amortizacion.AmrSldAnt +' ('+this.certification.money_literal+' bolivianos) . ';
                    let spl2 = d.splitTextToSize(p2,180);
                    d.text(x,y+10,spl2);
                    y+=10;
                    // d.text("INFORME  ",90,20);

                    let p3 = 'En cumplimiento con lo previsto en el Reglamento del Beneficio del Complemento Económico de la gestión 2018, en su Artículo 25° párrafo 2, y Reglamento de Prestamos 2019 en su Artículo 120°, en caso de amortizar su deuda con el beneficio del Complemento Econónmico.'
                    let spl3 = d.splitTextToSize(p3,180);
                    d.text(x,y+15,spl3);
                    y+=15;

                    if(this.certification.parrafo !='')
                    {
                        let splarrafo = d.splitTextToSize(this.certification.parrafo,180);
                        d.text(x,y+15,splarrafo);
                        y+=15;
                    }

                    let p35 = 'Mediante la nota presentada en fecha '+this.certification.fecha_texto+' Hoja de Tramite No. '+this.certification.hoja+', solicita que el '+this.certification.porcentaje+' del monto del Beneficio Complemento Económico 1er Semestre 2019 pase a cubrir '+this.certification.texto_mediante+' de su préstamo.'
                    let spl35 = d.splitTextToSize(p35,180);
                    d.text(x,y+15,spl35);
                    y+=15;
                    let p4 = 'El Registro en el Sistema de Prestamo de la amortización del monto de complemento económico, se distribuirá en la siguiente forma:'
                    let spl4 = d.splitTextToSize(p4,180);
                    d.text(x,y+15,spl4);
                    y+=15;
                    let columns = ["Distribución de capital e interés","",""];
                    let rows = [
                        {'name':'Capital','money':'Bs','monto':this.certification.amortizacion.AmrCap},
                        {'name':'Interés corriente','money':'Bs','monto':this.certification.amortizacion.AmrInt},
                        {'name':'Interés Penal','money':'Bs','monto':this.certification.amortizacion.AmrIntPen},
                        {'name':'Total','money':'Bs','monto':this.certification.amortizacion.AmrTotPag},           
                    ];
                    y+=10;
                    // var x = 20;
                    // var y =120;
                    // d.rect(x,y-22,90+22+22+22+22,6);
                    // d.text('Distribucion de cap',x+2,y+4-22);

                    for(var i=0;i< rows.length;i++)
                    {
                        console.log(''+i);
                        d.rect(x,y,90+22,6);
                        // d.rect(x+90,y,22,6);
                        d.rect(x+90+22,y,22,6);
                        d.rect(x+90+22+22,y,35,6);

                        d.text(''+rows[i].name,x+2,y+4);
                        //d.text(''+rows[i].cantidad,x+90+2,y+4);
                        d.text(''+rows[i].money, x+90+22+2,y+4);
                        d.text(''+rows[i].monto,x+90+22+22+2,y+4);
                        //x=x+6;
                        y=y+6;
                    }


                    let p5 = 'Con el comprobante de compensación de la amortización por la Unidad contabilidad,se validará en el Sistema de Préstamo de amortización.'
                    let spl5 = d.splitTextToSize(p5,180);
                    d.text(x,y+15,spl5);
                    y+=15;
                    let p6 = 'Es cuanto se certifica en honor a la verdad , para los fines consiguientes.'
                    let spl6 = d.splitTextToSize(p6,190);
                    d.text(x,y+15,spl6);
                    y+=15;
                    d.text("La Paz, "+this.certification.today_literal,130,y+15);
                    this.cadena = d.output('datauristring');
            });

          
           // $('iframe').attr('src', cadena);
        }
        // toggleOrder (filter) {
        //     this.filterName = filter;
        //     this.search(filter).then(()=>{
        //         this.pagination.sortBy = this.filterName; 
        //         this.pagination.descending = !this.pagination.descending
        //     });    
        // },
       
      
    },
    watch: {
        filterValue (fv) {      
            if (fv =='') {
                this.search();
            }
        }
    },
    computed:{
      
    }
}
</script>
